	<script>
		function addTrailingSlash(link) {
			return link.at(-1) !== "/" ? `${link}/` : link;
		}

		const links = document.querySelectorAll(".dst-nav-link");
		const currentLink = addTrailingSlash(window.location.href);
		for (const link of links) {
			if (addTrailingSlash(link.href) == currentLink) {
				link.classList.add("dst-nav-link-current");
				break;
			}
		}
	</script>
